mapper.select:
select * from (select rownum rn,t.* from (select b.*, u.user_id from islandboard b,userinfo u where b.user_no = u.no
and b.no=#{word}
and ${type} like '%${word}%'
order by b.no desc) t)
where rn >= #{first} and rn <= #{last}

select count(*) from islandboard b,userinfo u where b.user_no = u.no


		and b.no=#{word}


		and ${type} like '%${word}%'
